import React from 'react';
import Navbar from "../Components/Navbar.js";
import Footer from "../Components/Footer.js";
import PostTable from "../Components/PostTable.js";

//Change isAdmin to see student view

class ClassPage extends React.Component {
  constructor(props) {
    super(props);

    console.log("class ID");
    // console.log(this.props.location.state.classId); //cannot receive
    // console.log(this.props.classTitle);
    // console.log(this.props.classDescription);

    this.state  = {
        classPostsData: []
    };
  }

    componentDidMount() {
        fetch("https://api.myjson.com/bins/jfuqc") //fetch by this.props.classId
            .then(data => data.json())
            .then(data => this.setState({classPostsData: data}))
      }

    render() {
      console.log(this.props.location.state.classId)
        return(
          <div>
            <Navbar isLoggedIn={this.props.isLoggedIn} />

            <h1>this.props.classTitle</h1> 
            <p>this.props.classDescription</p>
            {this.state.classPostsData.map(post => {
              return (
                <PostTable
                  key={post.post_ID}
                  post_ID={post.post_ID}
                  post_Title={post.post_Title}
                  post_note={post.post_note}
                />
              );
            })}
            <Footer isLoggedIn={this.props.isLoggedIn} />
          </div>
        );
    }
}

export default ClassPage;
