import React, { Component } from "react";

class Comment extends Component {

  render() {
    console.log(this.props.commentList);



    return (
      <div>
      {this.props.commentList.map(function(comment, idx){
         return (
          <h1>{comment.text}</h1>
          )
       })}
      </div>
    );
  }
}

export default Comment;

