import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";

import LandingPage from "./Pages/LandingPage.js";
import AboutPage from "./Pages/AboutPage.js";
import PrivacyPage from "./Pages/PrivacyPage.js";
import ContactUsPage from "./Pages/ContactUsPage.js";
import WelcomePage from "./Pages/WelcomePage.js";
import ClassPage from "./Pages/ClassPage.js";
import ViewPostPage from "./Pages/ViewPostPage.js";

class App extends Component {
  constructor() {
    super();
    this.state = {
      isLoggedIn: false,
      user: []
    };
    this.setUserState = this.setUserState.bind(this);
    this.setIsLoggedInToTrue = this.setIsLoggedInToTrue.bind(this);
  }

  setIsLoggedInToTrue() {
    this.setState({isLoggedIn: true});
  }

  setUserState(e) {
    this.setState({user: e});

  }



  render() {
    return (
      <Router>
        <div className="backGroundColor">
          <Route path="/" exact render={() => <LandingPage isLoggedIn={this.state.isLoggedIn} user={this.state.user} setUserState={this.setUserState} setIsLoggedInToTrue={this.setIsLoggedInToTrue}/>} />
          <Route path="/about" exact render={() => <AboutPage isLoggedIn={this.state.isLoggedIn} user={this.state.user}/>} />
          <Route path="/contactus" exact render={() => <ContactUsPage isLoggedIn={this.state.isLoggedIn} user={this.state.user}/>} />
          <Route path="/privacy" exact render={() => <PrivacyPage isLoggedIn={this.state.isLoggedIn} user={this.state.user}/>} />
          <Route path="/welcome" exact render={() => <WelcomePage isLoggedIn={this.state.isLoggedIn} user={this.state.user}/>} />
          <Route path="/classpage/:classId" exact render={(props) => <ClassPage {...props} isLoggedIn={this.state.isLoggedIn} user={this.state.user}/>} />
          <Route path="/viewpost/:postId" exact render={(props) => <ClassPage {...props} isLoggedIn={this.state.isLoggedIn} user={this.state.user}/>} />


        </div>
      </Router>
    );
  }
}

export default App;
// npm install --save react-router-dom
// npm install --save bootstrap
