  //   console.log("first fetch");
  // }

  //   secondFetch() {
  //     console.log(this.state.jsonFile.password);
  //     if (this.state.jsonFile === []) {
  //       console.log("user doesn't exist");
  //       console.log(this.state.jsonFile.password);
  //     } else if (this.state.jsonFile.password === this.state.password) {
