import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./Login.css";

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form error being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });
  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });
  return valid;
};

class Register extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,
      lastName: null,
      studentID: null,
      email: null,
      password: null,
      avatar: null,
      formErrors: {
        firstName: "",
      lastName: "",
      studentID: "",
      email: "",
      password: "",
      avatar: "",
      }
    };
  }

  handleSubmit = (e) => {
    e.preventDefault();

    if (formValid(this.state)) {
      const fd = new FormData();
      fd.append('firstName', this.state.firstName);
      fd.append('lastName', this.state.lastName);
      fd.append('studentID', this.state.studentID);
      fd.append('email', this.state.email);
      fd.append('password', this.state.password);
      fd.append('avatar', this.state.avatar);

      const searchParams = new URLSearchParams();
      for (const pair of fd) {
        searchParams.append(pair[0], pair[1]);
      }

      fetch("http://localhost:8080/senioproject/addUser", {
        body: searchParams,
        method: POST
      });


    } else {
      console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
    }



  };

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };

    switch (name) {
      case "ndnuId":
        formErrors.ndnuId =
          value.length < 3 ? "minimum 3 characters required" : "";
        break;
      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "invalid email address";
        break;
      case "password":
        formErrors.password =
          value.length < 6 ? "minimum 6 characters required" : "";
        break;
      default:
        break;
    }
    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };

  render() {
    const { formErrors } = this.state;
    return (
      <div className="container">
        <div className="Wrapper float-right">
          <div className="form-wrapper float-right">
            <h3>Register</h3>
            <form onSubmit={this.handleSubmit} noValidate>

            <div className="firstName">
                <input
                  className={formErrors.firstName.length > 0 ? "error" : null}
                  placeholder="firstName"
                  type="text"
                  name="firstName"
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.ndnuId.length > 0 && (
                  <span className="errorMessage">{formErrors.firstName}</span>
                )}
              </div>

              <div className="lastName">
                <input
                  className={formErrors.lastName.length > 0 ? "error" : null}
                  placeholder="lastName"
                  type="text"
                  name="lastName"
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.ndnuId.length > 0 && (
                  <span className="errorMessage">{formErrors.lastName}</span>
                )}
              </div>

              <div className="studentID">
                <input
                  className={formErrors.studentID.length > 0 ? "error" : null}
                  placeholder="student ID"
                  type="text"
                  name="studentID"
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.ndnuId.length > 0 && (
                  <span className="errorMessage">{formErrors.studentID}</span>
                )}
              </div>

              <div className="email">
                <input
                  className={formErrors.email.length > 0 ? "error" : null}
                  placeholder="Email"
                  type="email"
                  name="email"
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.email.length > 0 && (
                  <span className="errorMessage">{formErrors.email}</span>
                )}
              </div>
              <div className="password">
                <input
                  className={formErrors.password.length > 0 ? "error" : null}
                  placeholder="Password"
                  type="password"
                  name="password"
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.password.length > 0 && (
                  <span className="errorMessage">{formErrors.password}</span>
                )}
              </div>

              <div className="avatar">
                <input
                  className={formErrors.avatar.length > 0 ? "error" : null}
                  placeholder="Avatar"
                  type="avatar"
                  name="avatar"
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.email.length > 0 && (
                  <span className="errorMessage">{formErrors.avatar}</span>
                )}
              </div>
              <div className="FormField">
                <label className="FormField__CheckboxLabel">
                  {" "}
                  <input
                    className="FormField__Checkbox"
                    type="checkbox"
                    name="hasAgreed"
                    value={this.state.hasAgreed}
                    onChange={this.handleChange}
                  />
                  Keep me logged in
                </label>
              </div>
              <div className="createAccount">
                <button type="submit" to="#">
                  Register
                </button>
              </div>
              <div className="createSumbit">
                <Link onClick={this.props.setIsRegisteredStateToTrue} to="/">
                  Already have an Acount?
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
